mapper.delete:
delete from fmall_order
where user_id = #{userId}
and order_no = #{orderNo}

mapper.select:
select

from fmall_order o,fmall_order_item oi
where o.order_no = oi.order_no

  and o.status = #{status}

and o.user_id = #{userId}

select

from fmall_order
where user_id = #{userId}
and order_no = #{orderNo}

select shipping_id
from fmall_order
where order_no = #{orderNo}
